import React from 'react'

export const BadgesList = () => {
    return (
        <div>
             <ul className="list-unstyled">
                                {this.state.data.map((badge) =>{
                                    return(

                                            <li key={badge.id}>
                                                <p>
                                                    {badge.firstName} {badge.lastName}
                                                </p>
                                            </li>
                                    )
                                })}
                            </ul>
        </div>
    )
}


export default BadgesList;
