import React from 'react'

export const BadgesList = () => {
    state = this.props.badges
    return (
        <div>
            <ul className="list-unstyled">
                {this.props.badges.map((badge) =>{
                    return(

                            <li key={badge.id}>
                                <p>
                                    {badge.firstName} {badge.lastName}
                                </p>
                            </li>
                    )
                })}
            </ul>
        </div>
    )
}


export default BadgesList;
